package chubatova.gradle.multi.module.app;

import chubatova.gradle.multi.module.list.LinkedList;

import static chubatova.gradle.multi.module.utilities.StringUtils.join;
import static chubatova.gradle.multi.module.utilities.StringUtils.split;
import static chubatova.gradle.multi.module.app.MessageUtils.getMessage;

import org.apache.commons.text.WordUtils;

public class App {
    public static void main(String[] args) {
        LinkedList tokens;
        tokens = split(getMessage());
        String result = join(tokens);
        System.out.println(WordUtils.capitalize(result));
    }
}
